        <AnimatePresence>
      {showElement && (
        <motion.div
          initial={{ scale: 0, originX: 0.5, originY: 0.5 }}
          animate={{ scale: 1 }}
          transition={{ duration: 1 }}
        >
                <Link to="obras">
                <div class="grid-item grid-item-item1">
                    <h1>Obras</h1>
                    <p>Explora as obras de Fernando Pessoa e seus Heter√≥nimos.</p>
