import { useState, useEffect } from 'react';
import { API_URL } from '../../api';
import { Swup } from 'swup';

const About = () => {
	const [aboutPageInfo, setAboutPageInfo] = useState(null);

	useEffect(() => {
		fetch(API_URL + 'pages?slug=about')
				.then(response => response.json())
				.then(result => {
					setAboutPageInfo(result)
				});
	}, []);

	if(!aboutPageInfo) {
		return null;
	}

	return (

    <main id="swup" class="transition-fade">
      <div class="grid-container">
        <a href="obras.html">
          <div class="grid-item grid-item-item1">
            <h1>Obras</h1>
            <p>Explora as obras de Fernando Pessoa e seus Heter√≥nimos.</p>
          </div>
        </a>
        <a href="vida.html">
          <div class="grid-item grid-item-item2">
            <h1>Vida</h1>
            <p>Descobre mais sobre a vida de Fernando Pessoa.</p>
          </div> 
        </a>
      </div>
    </main>

		);
}

export default About;
