            animate={{opacity: 1, width: "100vw"}}
            exit={{opacity: 0, x: window.innerWidth, transition: { duration: 0.5 }}}>
                <Link to="obras">
                <div class="grid-item grid-item-item1">
                    <h1>Obras</h1>
